    @Test
    public void directionTest() {

        PuzzleOne puzzle = new PuzzleOne();

        assertEquals(0, puzzle.getFloor("(())"));
        assertEquals(0, puzzle.getFloor("()()"));
        assertEquals(3, puzzle.getFloor("((("));
        assertEquals(3, puzzle.getFloor("(()(()("));
        assertEquals(3, puzzle.getFloor("))((((("));
        assertEquals(-1, puzzle.getFloor("())"));
        assertEquals(-1, puzzle.getFloor("))("));
        assertEquals(-3, puzzle.getFloor(")))"));
        assertEquals(-3, puzzle.getFloor(")())())"));

    }

